package loan;import java.io.*;/** models a customer: has some extras over the basic spec step 1A: *  customers can be assigned the next available id automatically */public class Customer implements Serializable{	private String name;	private double monthly_payments;	private Loan loan;        /** customer will be assigned unknown name and next available id number */    public Customer() {    	this("Name unknown");    }        /** named customer will be assigned next available id number */    public Customer(String name){    	setName(name);    	setMonthlyPayments(0.0);    	    }         /** named customer assigned a specific id: no checking done */     public Customer(String name, double mpayment){    	setName(name);    	setMonthlyPayments(mpayment);    }        public void setName(String name){    	this.name = name;    }        public void setMonthlyPayments(double mpayment){    	this.monthly_payments = mpayment;    }        public String getName() {    	return name;    }        /** returns the id of a particular customer */    public double getMonthlypayments() {    	return monthly_payments;    }        public String toString() {    	return "Name: " + getName() + " Payments: " + getMonthlypayments();    }     public void takeoutloan(Loan loan){             monthly_payments = loan.getMonthlyPayment();    }            }